import React, { useState, useEffect } from 'react';
import './Lockscreen.css';

const Lockscreen = () => {
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000); // Update every minute

    return () => clearInterval(timer); // Cleanup the interval on component unmount
  }, []);

  const formatTime = () => {
    const options = { hour: '2-digit', minute: '2-digit' }; // Only hour and minute
    return currentTime.toLocaleTimeString(undefined, options);
  };

  const formatDate = () => {
    const options = { weekday: 'long', day: 'numeric', month: 'long' }; // Only date
    return currentTime.toLocaleDateString(undefined, options);
  };

  return (
    <div className="background-container">
      <div className="lockscreen-content">

v        <h1 className="time-text">{formatTime()}</h1>
      </div>
    </div>
  );
};

export default Lockscreen;
