import React, { useState } from 'react';
import Main from './components/Main/Main';
import Lockscreen from './components/Lockscreen/Lockscreen'; // Import the new component
import './App.css';

function App() {
  const [showMain, setShowMain] = useState(false); // State to toggle components

  const handleInteraction = () => {
    setShowMain(true); // Switch to Main component on any interaction
  };

  return (
    <div
      className="App"
      onClick={handleInteraction} // Handle click events
    >
      {showMain ? <Main /> : <Lockscreen />} {/* Conditionally render components */}
    </div>
  );
}

export default App;
